import { jsx } from '@compiled/react';
import { Box } from '@atlaskit/primitives/compiled';
import { cssMap } from '@atlaskit/css';

const containerWrapperStyles = cssMap({
	root: {
		position: 'relative',
		cursor: 'move',
		userSelect: 'none',
		overflow: 'hidden',
	},
});

const checkeredBg =
	'data:image/png;base64,';

export type ContainerWrapperProps = React.HTMLAttributes<HTMLDivElement> & {
	width: number;
	height: number;
	margin: number;
};

export const ContainerWrapper = ({
	width,
	height,
	margin,
	children,
	...props
}: ContainerWrapperProps) => {
	return (
		<Box
			id={'container-wrapper'}
			xcss={containerWrapperStyles.root}
			style={{
				background: `url('${checkeredBg}')`,
				width: `${width !== undefined ? width! + margin * 2 : 0}px`,
				height: `${height !== undefined ? height! + margin * 2 : 0}px`,
			}}
			{...props}
		>
			{children}
		</Box>
	);
};
